mapper.insert:
INSERT into authority
(EMP_CODE,  emp_Name, dep_code, pos_code, permission_edit, department_management , rank_edit)
VALUES(#{empCode}, #{empName}, #{depCode}, #{posCode},#{permissionEdit}, #{departmentManagement},#{rankEdit});

mapper.update:
UPDATE authority
set  NOTICE=#{notice}, DOCUMENT=#{document}, PERS_INFO=#{persInfo}, SCHEDULE=#{schedule},
permission_edit=#{permissionEdit}, department_management=#{departmentManagement} , rank_edit=#{rankEdit}
where  EMP_CODE= #{empCode}
