mapper.insert:
INSERT INTO employee

(EMP_CODE, EMP_NAME, EMP_PASS, DEP_CODE, PHONE_NUM, EMP_MAIL, EMP_RRN , pos_code,cor_code)
VALUES(#{empCode},  #{empName}, #{empPass},#{depCode},#{phoneNum}, #{empMail},#{empRrn},#{posCode},#{corCode});

INSERT INTO authority
(EMP_CODE, EMP_NAME, DEP_CODE,permission_edit, department_management,rank_edit)
VALUES(#{empCode},#{empName},#{depCode},1, 1,1);

mapper.select:
SELECT COUNT(*) AS CNT
FROM employee
WHERE EMP_CODE = #{empCode}
